import 'bootstrap/dist/css/bootstrap.min.css';
import {BsFillCalendar2CheckFill} from "react-icons/bs"
import {Container, Row, Col, Card, ListGroup} from 'react-bootstrap';
import Search from './components/Search';
import AddAppointment from './components/AddAppointment';
import appointment from './data.json';
// import './App.css';

function App() {
  return (
    <div className="App">
      <Container>
        <Row>
          <Col>
          <h1 className='text-center fw-light mt-3'> <BsFillCalendar2CheckFill/> Appointment App</h1>

          </Col>
        </Row>
        <Row className='justify-content-center'>
           <AddAppointment />
        </Row>
        <Row className='justify-content-center'>
          <Col md={4}>
          <Search />
          </Col>
        </Row>
        <Row className='justify-content-center'>
          <Col md="8">
            <Card className='md-3'>
              <Card.Header>Appointments</Card.Header>
              <ListGroup variant='flush'>
                {appointment.map((appt)=>{
                  return(
                  <ListGroup.Item>
                    <p><small>Date: {appt.appDate}</small></p>
                    <p><small>First Name: {appt.firstName}</small></p>
                    <p><small>Last Name: {appt.lastName}</small></p>
                    <p><small>Note: {appt.aptNote}</small></p>
                  </ListGroup.Item>
                  );

                })}
              </ListGroup>
            </Card>
          </Col>
        </Row>
      </Container>

    </div>
  );
}

export default App;
