import 'bootstrap/dist/css/bootstrap.min.css';
import {BsFillCalendar2CheckFill} from "react-icons/bs"
import {Container, Row, Col, Card, ListGroup, InputGroup, Form} from 'react-bootstrap';
import Search from './components/Search';
import AddAppointment from './components/AddAppointment';
import appointmentList from './data.json';
import AppointmentInfo from './components/AppointmentInfo';
import React, {useState, useEffect} from 'react';
// import './App.css';

function App() {
    const [changer, setChanger] = useState(false);


  return (
    <div className="App">
      <Container>
      <InputGroup className="mb-3">
        <InputGroup.Checkbox onChange={(()=> setChanger((changer)=> !changer))} aria-label="Checkbox for following text input" />
        <Form.Control aria-label="Text input with checkbox" value={changer} />
      </InputGroup>
        <Row>
          <Col>
          <h1 className='text-center fw-light mt-3'> 
          <BsFillCalendar2CheckFill/> 
          <InputGroup.Checkbox onChange={(()=> setChanger((changer)=> !changer))} />
          Appointment App</h1>

          </Col>
        </Row>
        <Row className='justify-content-center'>
           <AddAppointment />
        </Row>
        <Row className='justify-content-center'>
          <Col md={4}>
          <Search />
          </Col>
        </Row>
        <Row className='justify-content-center'>
          <Col md="8">
            <Card className='md-3'>
              <Card.Header>Appointments</Card.Header>
              <ListGroup variant='flush'>
                {appointmentList.map(appt=>(
                 <AppointmentInfo key={appt.id} appt={appt} />
                ))}
              </ListGroup>
            </Card>
          </Col>
        </Row>
      </Container>

    </div>
  );
}

export default App;
